mapper.sql:
FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY KEYNO ${sortDirect}
ORDER BY AP_NAME ${sortDirect}
ORDER BY TYPENAME ${sortDirect}
ORDER BY PRICE ${sortDirect}
ORDER BY PM_NAME ${sortDirect}
ORDER BY UI_NAME ${sortDirect}
ORDER BY AP_REGDT ${sortDirect}
ORDER BY AP_USE_NM ${sortDirect}
ORDER BY AP_KEYNO DESC
) RNUM FROM ( SELECT TO_NUMBER(SUBSTR(AP_KEYNO,-10)) KEYNO , AP_KEYNO , AP_NAME , CS.SC_CODENM AS TYPENAME , CASE AP_PRICE WHEN '0' THEN '무료' ELSE TO_CHAR(AP_PRICE,'999,999,999') || '원' END PRICE , P.PM_NAME , U.UI_NAME , TO_CHAR(AP_REGDT,'YYYY-MM-dd HH24:mi:ss') AP_REGDT , CASE WHEN AP.AP_USE = 'N' THEN '사용안함' WHEN AP.AP_USE = 'Y' THEN '사용함' END AS AP_USE_NM , TO_CHAR(SYSDATE,'YYYY-MM-dd') NOW_DATE , CASE WHEN AP.AP_DEADLINE = 'N' THEN '진행중' WHEN AP.AP_DEADLINE = 'Y' THEN '마감' END AS DEADLINE , AP_APPLY_EN_DATE , AP_ENDT FROM T_APPLICAITON_MANAGER AP LEFT JOIN T_PLACE_MANAGER P ON AP.AP_PLACE = P.PM_KEYNO LEFT JOIN T_USERINFO U ON AP.AP_REGNM = U.UI_KEYNO LEFT JOIN T_COMMON_CODE_SUB CS ON AP.AP_TYPE = CS.SC_KEYNO WHERE AP_USE != 'D' ) AB WHERE 1=1
AND KEYNO LIKE '%' || #{item.searchKeyword} || '%'
AND AP_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND TYPENAME = #{item.searchKeyword}
AND PRICE LIKE '%' || #{item.searchKeyword} || '%'
AND PM_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND UI_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND AP_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND AP_USE_NM LIKE '%' || #{item.searchKeyword} || '%'
AND ( KEYNO LIKE '%' || #{item.searchKeyword} || '%' OR AP_NAME LIKE '%' || #{item.searchKeyword} || '%' OR TYPENAME = #{item.searchKeyword} OR PRICE LIKE '%' || #{item.searchKeyword} || '%' OR PM_NAME LIKE '%' || #{item.searchKeyword} || '%' OR UI_NAME LIKE '%' || #{item.searchKeyword} || '%' OR AP_REGDT LIKE '%' || #{item.searchKeyword} || '%' OR AP_USE_NM LIKE '%' || #{item.searchKeyword} || '%' )
)AA

mapper.select:
SELECT	* 


WHERE 1=1
AND 	RNUM BETWEEN  #{firstIndex} AND #{lastIndex}

WITH V_TEMP1 AS ( SELECT A.* FROM ( SELECT CASE WHEN AP.AP_USE = 'N' THEN '사용안함' WHEN AP.AP_USE = 'Y' THEN '사용함' END AS AP_USE_NM , TO_CHAR(SYSDATE,'YYYY-MM-DD') NOW_DATE , CASE WHEN AP.AP_DEADLINE = 'N' THEN '진행중' WHEN AP.AP_DEADLINE = 'Y' THEN '마감' END AS DEADLINE , AP.* , P.PM_NAME , U.UI_NAME , CS.SC_CODENM AS TYPENAME ,ROW_NUMBER() OVER(PARTITION BY AP.AP_KEYNO ORDER BY AP.AP_KEYNO) AS RN FROM T_APPLICAITON_MANAGER AP LEFT JOIN T_PLACE_MANAGER P ON AP.AP_PLACE = P.PM_KEYNO LEFT JOIN T_USERINFO U ON AP.AP_REGNM = U.UI_KEYNO LEFT JOIN T_APPLICAITON_DISCOUNT_SELECT ADS ON AP.AP_KEYNO = ADS.AP_KEYNO LEFT JOIN T_COMMON_CODE_SUB CS ON AP.AP_TYPE = CS.SC_KEYNO WHERE AP_USE != 'D'
AND AP.AP_KEYNO = #{AP_KEYNO}
ORDER BY AP.AP_KEYNO DESC ) A WHERE A.RN = 1 ) , V_TEMP2 AS ( SELECT LISTAGG(AD_KEYNO,',') WITHIN GROUP (ORDER BY AD_KEYNO) AS AD_KEYNO ,AP.AP_KEYNO FROM T_APPLICAITON_MANAGER AP LEFT JOIN T_PLACE_MANAGER P ON AP.AP_PLACE = P.PM_KEYNO LEFT JOIN T_USERINFO U ON AP.AP_REGNM = U.UI_KEYNO LEFT JOIN T_APPLICAITON_DISCOUNT_SELECT ADS ON AP.AP_KEYNO = ADS.AP_KEYNO LEFT JOIN T_COMMON_CODE_SUB CS ON AP.AP_TYPE = CS.SC_KEYNO WHERE AP_USE != 'D'
AND AP.AP_KEYNO = #{AP_KEYNO}
GROUP BY AP.AP_KEYNO ORDER BY AP.AP_KEYNO DESC ) SELECT A.*,B.AD_KEYNO FROM V_TEMP1 A INNER JOIN V_TEMP2 B ON A.AP_KEYNO = B.AP_KEYNO

mapper.sql:
FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY DIVISION ${sortDirect}
ORDER BY AP_NAME ${sortDirect}
ORDER BY UI_NAME ${sortDirect}
ORDER BY SEQ ${sortDirect}
ORDER BY PRICE ${sortDirect}
ORDER BY APP_REGDT ${sortDirect}
ORDER BY STATUS ${sortDirect}
ORDER BY COUNT DESC
) RNUM FROM ( SELECT ROWNUM AS "COUNT" , A.* , DECODE(A.DIVISION, '신청', A.APP_ST_DATE || ' / (' || APP_SEQUENCE || ')' || APP_ST_TIME, A.APP_ST_DATE || ' / ' || APP_ST_TIME) SEQ , CASE TOTALPRICE WHEN 0 THEN '무료' ELSE TO_CHAR(TOTALPRICE,'999,999,999') || '원' END PRICE FROM ( SELECT TO_NUMBER(SUBSTR(APP.APP_AP_KEYNO,-10)) APP_AP_KEYNO , AM.AP_NAME , U.UI_REP_NAME UI_NAME , TO_NUMBER(SUBSTR(APP.APP_KEYNO,-10)) APP_KEYNO , TO_CHAR(APP.APP_REGDT, 'YYYY-MM-dd HH24:mi:ss') APP_REGDT , APP.APP_ST_DATE , APP.APP_SEQUENCE , APP.APP_ST_TIME , CASE WHEN APP.APP_DIVISION = 'SC_0000000342' THEN '신청' WHEN APP.APP_DIVISION = 'SC_0000000343' THEN '강좌' END AS DIVISION , CASE WHEN APP.APP_STATUS = 'SC_0000000344' THEN '결제대기' WHEN APP.APP_STATUS = 'SC_0000000345' THEN '신청완료' WHEN APP.APP_STATUS = 'SC_0000000346' THEN '신청취소' WHEN APP.APP_STATUS = 'SC_0000000347' THEN '대기자' WHEN APP.APP_STATUS = 'SC_0000000348' THEN '결제만료' WHEN APP.APP_STATUS = 'SC_0000000349' THEN '관람완료' END AS STATUS , NVL(SUM(APD.APD_PRICE*APD.APD_CNT) OVER(PARTITION BY APD.APD_APP_KEYNO ORDER BY APD.APD_APP_KEYNO),0) AS TOTALPRICE , ROW_NUMBER() OVER(PARTITION BY APD.APD_APP_KEYNO ORDER BY APD.APD_APP_KEYNO) AS RN FROM T_APPLICATION_PARTICIPATE APP
LEFT JOIN T_APPLICATION_USER AU ON APP.APP_APU_KEYNO = AU.APU_KEYNO
LEFT JOIN T_USERINFO U ON AU.APU_UI_KEYNO = U.UI_KEYNO
LEFT JOIN T_APPLY_PARTICIPATE_DISCOUNT APD ON APP.APP_KEYNO = APD.APD_APP_KEYNO
LEFT JOIN T_APPLICAITON_MANAGER AM ON APP.APP_AP_KEYNO = AM.AP_KEYNO WHERE AM.AP_KEYNO IS NOT NULL ORDER BY APP.APP_REGDT ) A WHERE A.RN = 1 ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND DIVISION = #{item.searchKeyword}
AND AP_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND UI_NAME LIKE '%' || #{item.searchKeyword} || '%'
AND SEQ LIKE '%' || #{item.searchKeyword} || '%'
AND PRICE LIKE '%' || #{item.searchKeyword} || '%'
AND APP_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND STATUS = #{item.searchKeyword}
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR DIVISION = #{item.searchKeyword} OR AP_NAME LIKE '%' || #{item.searchKeyword} || '%' OR UI_NAME LIKE '%' || #{item.searchKeyword} || '%' OR SEQ LIKE '%' || #{item.searchKeyword} || '%' OR PRICE LIKE '%' || #{item.searchKeyword} || '%' OR APP_REGDT LIKE '%' || #{item.searchKeyword} || '%' OR STATUS = #{item.searchKeyword} )
)AA

