mapper.sql:
FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY AD_NAME ${sortDirect}
ORDER BY AD_USE ${sortDirect}
ORDER BY MONEY ${sortDirect}
ORDER BY AD_COMENT ${sortDirect}
ORDER BY AD_DEFAULT_YN ${sortDirect}
ORDER BY COUNT DESC
) RNUM FROM ( SELECT ROWNUM AS COUNT , A.* FROM( SELECT DECODE(AD.AD_TYPE,'A', AD.AD_MONEY||'%', AD.AD_MONEY||'원') MONEY , AD.AD_NAME , AD.AD_COMENT , CASE AD_USE WHEN 'Y' THEN 'O' WHEN 'N' THEN 'X' END AD_USE , CASE AD_DEFAULT_YN WHEN 'Y' THEN 'O' WHEN 'N' THEN 'X' END AD_DEFAULT_YN , TO_NUMBER(SUBSTR(AD_KEYNO,-10)) AD_KEYNO FROM T_APPLICATION_DISCOUNT AD WHERE AD_USE != 'D' AND AD_TYPE != 'C' ORDER BY AD_KEYNO DESC )A ) AB WHERE 1=1
AND COUNT LIKE '%'||#{item.searchKeyword}||'%'
AND AD_NAME LIKE '%'||#{item.searchKeyword}||'%'
AND AD_USE = #{item.searchKeyword}
AND MONEY LIKE '%'||#{item.searchKeyword}||'%'
AND AD_COMENT LIKE '%'||#{item.searchKeyword}||'%'
AND AD_DEFAULT_YN = #{item.searchKeyword}
AND ( COUNT LIKE '%'||#{item.searchKeyword}||'%' OR AD_NAME LIKE '%'||#{item.searchKeyword}||'%' OR AD_USE = #{item.searchKeyword} OR MONEY LIKE '%'||#{item.searchKeyword}||'%' OR AD_COMENT LIKE '%'||#{item.searchKeyword}||'%' OR AD_DEFAULT_YN = #{item.searchKeyword} )
)AA

