mapper.select:
SELECT MVD_DEL_YN
FROM T_MENU_VIEW_DATA
WHERE MVD_KEYNO = #{MVD_KEYNO}

SELECT SUBSTR(SUBSTR(MN_URL,0,INSTR(MN_URL,'/',2)-1),2) MVD_TILES
, M.MVD_DATA FROM T_MENU_VIEW_DATA M LEFT JOIN T_MENU_MANAGER MN ON M.MVD_MN_KEYNO = MN.MN_KEYNO AND MN.MN_DEL_YN = 'N' WHERE MVD_KEYNO = #{MVD_KEYNO}
, (SELECT MVH_DATA FROM T_MENU_VIEW_DATA_HISTORY WHERE MVH_KEYNO = #{MVH_KEYNO}) MVD_DATA FROM T_MENU_VIEW_DATA M LEFT JOIN T_MENU_MANAGER MN ON M.MVD_MN_KEYNO = MN.MN_KEYNO AND MN.MN_DEL_YN = 'N' WHERE MVD_KEYNO = (SELECT MVH_MVD_KEYNO FROM T_MENU_VIEW_DATA_HISTORY WHERE MVH_KEYNO = #{MVH_KEYNO})


	SELECT 		MVD_DATA 
	FROM 		T_MENU_VIEW_DATA 
	WHERE 		MVD_KEYNO	=	#{MVD_KEYNO}
	UNION ALL


	(
	SELECT MVD_DATA FROM (
		SELECT MVH_DATA MVD_DATA
				,ROW_NUMBER() OVER(ORDER BY MVH_STDT DESC) AS RN
		FROM		T_MENU_VIEW_DATA_HISTORY
		WHERE		MVH_MVD_KEYNO	= (
										SELECT MVH_MVD_KEYNO 
										FROM T_MENU_VIEW_DATA_HISTORY 
										WHERE MVH_KEYNO	=  #{MVH_KEYNO}
									  )
		AND			MVH_DEL_YN		=	'N'
        AND			MVH_KEYNO		<=  #{MVH_KEYNO}
		)
        WHERE RN <= 2
	)

mapper.update:
UPDATE  T_MENU_VIEW_DATA_HISTORY
SET		MVH_DEL_YN = 'Y'
WHERE 	MVH_KEYNO	=	#{MVH_KEYNO}

UPDATE  T_MENU_VIEW_DATA
SET		MVD_DEL_YN = #{MVD_DEL_YN}
WHERE 	MVD_KEYNO	=	#{MVD_KEYNO}

mapper.sql:
FROM( SELECT A.* , ROW_NUMBER() OVER (
ORDER BY MN_NAME ${sortDirect}
ORDER BY MVD_URL ${sortDirect}
ORDER BY MVD_REGNM ${sortDirect}
ORDER BY MVD_REGDT ${sortDirect}
ORDER BY MVD_KEYNO DESC
) RNUM FROM ( SELECT MVD_KEYNO , MM.MN_NAME , CASE WHEN MM2.MN_URL IS NULL THEN MM.MN_FORWARD_URL WHEN MM.MN_URL IS NOT NULL THEN MM2.MN_URL END MVD_URL , UI_ID MVD_REGNM , TO_CHAR(MVD_REGDT,'YYYY-MM-dd HH:mm:ss') AS MVD_REGDT FROM T_MENU_VIEW_DATA MV LEFT JOIN T_MENU_MANAGER MM ON MV.MVD_MN_HOMEDIV_C = MM.MN_KEYNO LEFT JOIN T_MENU_MANAGER MM2 ON MV.MVD_MN_KEYNO = MM2.MN_KEYNO LEFT JOIN T_USERINFO UI ON MV.MVD_REGNM = UI.UI_KEYNO WHERE MVD_DEL_YN = 'N' AND MM.MN_NAME IS NOT NULL ) A WHERE 1 = 1
AND MN_NAME = #{item.searchKeyword}
AND MVD_URL LIKE '%' || #{item.searchKeyword} || '%'
AND MVD_REGNM LIKE '%' || #{item.searchKeyword} || '%'
AND MVD_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND ( MN_NAME = #{item.searchKeyword} OR MVD_URL LIKE '%' || #{item.searchKeyword} || '%' OR MVD_REGNM LIKE '%' || #{item.searchKeyword} || '%' OR MVD_REGDT LIKE '%' || #{item.searchKeyword} || '%' )
) AA

mapper.select:

    	SELECT	*


	 	WHERE 1=1
	 	AND 	RNUM BETWEEN  #{firstIndex} AND #{lastIndex}

SELECT 'MENU_'||LTRIM(SUBSTR(MN_KEYNO,INSTR(MN_KEYNO,'_',1)+1),'0') VIEWNAME , MVD_KEYNO, MVD_DATA, MVD_REGDT , (SELECT UI_NAME FROM T_USERINFO WHERE UI_KEYNO = MVD.MVD_REGNM) MVD_REGNM , MVD_MODDT , (SELECT UI_NAME FROM T_USERINFO WHERE UI_KEYNO = MVD.MVD_MODNM) MVD_MODNM , MN.MN_KEYNO, MN.MN_HOMEDIV_C , MN.MN_URL MVD_URL , SUBSTR(SUBSTR(MN_URL,0,INSTR(MN_URL,'/',2)-1),2) MVD_TILES , MVD.MVD_EDITOR_TYPE FROM T_MENU_VIEW_DATA MVD LEFT JOIN T_MENU_MANAGER MN ON MVD.MVD_MN_KEYNO = MN.MN_KEYNO WHERE 1=1
AND ${column} = #{value}
AND MVD_KEYNO = #{MVD_KEYNO}

SELECT 	CASE WHEN MVD_MODDT IS NULL THEN MVD_REGDT ELSE MVD_MODDT END REGDT
FROM 	T_MENU_VIEW_DATA
WHERE 	MVD_KEYNO 	= 	#{MVD_KEYNO}

SELECT MVD_KEYNO
FROM T_MENU_VIEW_DATA
WHERE MVD_MN_KEYNO = #{MN_KEYNO}

SELECT NVL(MAX(MVH_VERSION),0)
FROM T_MENU_VIEW_DATA_HISTORY
WHERE MVH_MVD_KEYNO = #{MVD_KEYNO}
