mapper.insert:
	 INSERT  INTO  T_HOME_MANAGER
	 (
	  	  HM_KEYNO
		, HM_TILES
		, HM_MENU_DEPTH
		, HM_MN_HOMEDIV_C
		, HM_DEL_YN
		, HM_FAVICON
		, HM_LANG
		, HM_TITLE

	 )
	 VALUES
	 (
	  	  #{HM_KEYNO}
    	, #{HM_TILES}
    	, #{HM_MENU_DEPTH}
    	, #{HM_MN_HOMEDIV_C}
    	, 'N'
    	, #{HM_FAVICON}
    	, #{HM_LANG}
    	, #{HM_TITLE}
	 )

mapper.update:
UPDATE 	  T_HOME_MANAGER
SET		  HM_MENU_DEPTH	=	#{HM_MENU_DEPTH}
		, HM_FAVICON   =   #{HM_FAVICON}
		, HM_LANG		=	#{HM_LANG}
		, HM_TITLE		=	#{HM_TITLE}
WHERE	  HM_KEYNO		=	#{HM_KEYNO}

UPDATE	T_MENU_MANAGER
SET		MN_NAME		=	#{MN_NAME}
WHERE	MN_KEYNO	=	#{MN_KEYNO}

UPDATE	T_MENU_MANAGER
SET		MN_ORDER	=	#{MN_ORDER}
WHERE	MN_KEYNO	=	#{MN_KEYNO}

UPDATE	T_MENU_MANAGER
SET		MN_URL	=	NVL2(#{MN_URL},#{MN_URL},'')||NVL2(MN_URL,REPLACE(MN_URL,SUBSTR(MN_URL,1, INSTR(MN_URL,'/',1,2)-1),''),'')
WHERE	MN_HOMEDIV_C	=	#{MN_KEYNO}

UPDATE	T_HOME_MANAGER
SET		HM_DEL_YN	=	'Y'
WHERE	HM_KEYNO	=	#{HM_KEYNO}

UPDATE	T_MENU_MANAGER
SET		MN_DEL_YN		=	'Y'
	  , MN_DELDT		=	SYSDATE
	  , MN_MODNM		=	#{MN_MODNM}
WHERE	MN_HOMEDIV_C	=	#{MN_KEYNO}

UPDATE T_HOME_AUTHORITY_MANAGER SET HAM_DEFAULT_URL = CASE
WHEN HAM_KEYNO = #{item.HAM_KEYNO} THEN #{item.HAM_DEFAULT_URL}
ELSE HAM_DEFAULT_URL END WHERE HM_KEYNO = #{HM_KEYNO}

mapper.insert:
INSERT ALL

INTO T_HOME_AUTHORITY_MANAGER (
	  HAM_KEYNO
	, HM_KEYNO
	, UIA_KEYNO
	, HAM_DEFAULT_URL
)
VALUES
(
	  #{item.HAM_KEYNO}
	, #{HM_KEYNO}
	, #{item.UIA_KEYNO}
	, #{item.HAM_DEFAULT_URL}
)

SELECT * FROM DUAL

mapper.select:
SELECT     HM_MENU_DEPTH
FROM     T_HOME_MANAGER
WHERE     HM_MN_HOMEDIV_C        =    #{MN_HOMEDIV_C}

SELECT 	SUBSTR(MN_URL,2) TILES
FROM 	T_MENU_MANAGER
WHERE 	MN_KEYNO = #{MN_KEYNO}
