mapper.sql:
FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY SM_TITLE ${sortDirect}
ORDER BY SM_REGNM ${sortDirect}
ORDER BY SM_STARTDT ${sortDirect}
ORDER BY SM_ENDDT ${sortDirect}
ORDER BY CNT ${sortDirect}
ORDER BY MN_NAME ${sortDirect}
ORDER BY COUNT DESC
) RNUM FROM ( SELECT ROWNUM AS COUNT , B.* FROM ( SELECT A.CNT , B.*, MN.MN_NAME FROM ( SELECT SM_KEYNO , COUNT(SRM_SM_KEYNO) CNT FROM T_SURVEY_MGR SM LEFT JOIN T_SURVEY_RESULT_MGR SRM ON SM.SM_KEYNO = SRM.SRM_SM_KEYNO WHERE SM_DELYN = 'N' GROUP BY SM_KEYNO ) A LEFT JOIN T_SURVEY_MGR B ON A.SM_KEYNO = B.SM_KEYNO LEFT JOIN T_MENU_MANAGER MN ON B.SM_MN_KEYNO = MN.MN_KEYNO ORDER BY B.SM_REGDT ) B ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND SM_TITLE LIKE '%' || #{item.searchKeyword} || '%'
AND SM_REGNM LIKE '%' || #{item.searchKeyword} || '%'
AND SM_STARTDT LIKE '%' || #{item.searchKeyword} || '%'
AND SM_ENDDT LIKE '%' || #{item.searchKeyword} || '%'
AND CNT LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR SM_TITLE LIKE '%' || #{item.searchKeyword} || '%' OR SM_REGNM LIKE '%' || #{item.searchKeyword} || '%' OR SM_STARTDT LIKE '%' || #{item.searchKeyword} || '%' OR SM_ENDDT LIKE '%' || #{item.searchKeyword} || '%' OR CNT LIKE '%' || #{item.searchKeyword} || '%' )
)AA

mapper.select:
    	SELECT	* 


		WHERE 1=1
		AND 	RNUM BETWEEN  #{firstIndex} AND #{lastIndex}

mapper.update:
    	UPDATE T_SURVEY_MGR
   		SET  	 SM_TITLE		=	#{SM_TITLE}
	   			,SM_EXP			=	#{SM_EXP}
	   			,SM_STARTDT		=	#{SM_STARTDT}
	   			,SM_ENDDT		=	#{SM_ENDDT}
	   			,SM_IDYN		=	#{SM_IDYN}
	   			,SM_CNT_TYPE	=	#{SM_CNT_TYPE}
	   			,SM_MN_KEYNO	=	#{SM_MN_KEYNO}
    	WHERE SM_KEYNO = #{SM_KEYNO}

    	UPDATE T_SURVEY_QUESTION
    	SET		 SQ_ST_TYPE		=	#{SQ_ST_TYPE}
    			,SQ_QUESTION	=	#{SQ_QUESTION}

				 ,SQ_NUM		=	#{SQ_NUM}


    			,SQ_ALIGN		=	#{SQ_ALIGN}

    	WHERE SQ_KEYNO = #{SQ_KEYNO}

UPDATE T_SURVEY_QUESTION_OPTION
SET		 SQO_OPTION	=	#{SQO_OPTION}
		,SQO_NUM	=	#{SQO_NUM}

		,SQO_VALUE	=	#{SQO_VALUE}

WHERE SQO_KEYNO = #{SQO_KEYNO}

UPDATE T_SURVEY_MGR
SET SM_DELYN = 'Y'
WHERE SM_KEYNO = #{SM_KEYNO}

mapper.select:
UPDATE T_SURVEY_MGR
SET SM_PANEL_CNT = SM_PANEL_CNT + 1
WHERE SM_KEYNO = #{SM_KEYNO}
