mapper.select:
SELECT A.*, TC.TC_SUMMARY , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_CHANGENM,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') TC_FS_PATH , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_THUMBNAIL,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') TC_FS_THUMBNAIL_PATH FROM T_TOURISM_COMMON TC RIGHT JOIN ( SELECT C.* FROM( SELECT M4.*, M3.MN_TOURKEY FROM T_MENU_MANAGER M3 RIGHT JOIN ( SELECT * FROM ( SELECT A.*, @rownum:=@rownum+1 as RNUM FROM( SELECT MN_KEYNO, MN_NAME, MN_ORDER, MN_URL FROM T_MENU_MANAGER MN WHERE MN_MAINKEY = #{MN_MAINKEY} AND MN_DEL_YN = 'N' AND MN_USE_YN = 'Y' ) A ,(SELECT @rownum:=0)TMP ) B
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
) M4 ON M3.MN_MAINKEY = M4.MN_KEYNO GROUP BY M4.MN_KEYNO ) C ) A ON TC.TC_KEYNO = A.MN_TOURKEY LEFT JOIN T_COMMON_FILE_SUB FS ON TC.TC_THUMBNAIL = FS.FS_KEYNO ORDER BY MN_ORDER

    	SELECT COUNT(*)
		FROM T_TOURISM_LODGMENT TL
		LEFT JOIN T_TOURISM_COMMON  TC
		ON TL.TL_TC_KEYNO = TC.TC_KEYNO
		WHERE TC.TC_USE = 'Y'


				AND		TL_AREA = #{TL_AREA}


				AND		TL_DIVISION = #{TL_DIVISION}


				AND		TL_DIVISION IN 

				#{item.TL_DIVISION}





			AND		 	(   TC_TITLE			 	   LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
	  				  	 OR REPLACE(TC_TITLE,' ','') 	   LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
	  				  	 OR ${addressCol}		  	   LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
	  				  	 OR REPLACE(${addressCol},' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
	  			   		)		


			AND		 (	TC_TITLE			  LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
					 OR REPLACE(TC_TITLE,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
					 )


			AND		 ( ${addressCol}		LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
					OR REPLACE(${addressCol},' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%')
					)

SELECT A.*, @rownum:=@rownum+1 as RNUM FROM( SELECT TC.TC_KEYNO , TC.TC_TITLE , TC.TC_CONTACTS , TC.TC_CONTACT , TC.TC_CONTACT2 , TL.TL_TEL , TL_EUB , TL_BEDROOM_CNT , TL_ONDOLROOM_CNT , TL_GROUPROOM_CNT , TL_OWNER , IF(TC.TC_ADDR IS NOT NULL, CONCAT(TC.TC_ADDR,' ',TC.TC_ADDR2),'') ADDR , TC.TC_SUMMARY , TC.TC_REGDT , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_CHANGENM,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') FS_PATH , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_THUMBNAIL,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') FS_THUMBNAIL_PATH FROM T_TOURISM_LODGMENT TL LEFT JOIN T_TOURISM_COMMON TC ON TL.TL_TC_KEYNO = TC.TC_KEYNO LEFT JOIN T_COMMON_FILE_SUB FS ON TC.TC_THUMBNAIL = FS.FS_KEYNO WHERE TC.TC_USE = 'Y'
AND TL_AREA = #{TL_AREA}
AND TL_DIVISION = #{TL_DIVISION}
AND TL_DIVISION IN
#{item.TL_DIVISION}
AND ( TC_TITLE LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_TITLE,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR ${addressCol} LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(${addressCol},' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
AND ( TC_TITLE LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_TITLE,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
AND ( ${addressCol} LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(${addressCol},' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
) A ,(SELECT @rownum:=0)TMP ORDER BY ${OrderType}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT @rownum:=@rownum+1 as RNUM , B.* FROM ( SELECT A.* FROM( SELECT TC.TC_KEYNO, TC.TC_TITLE, TC.TC_CONTACTS, TR.*, TC.TC_CONTACT, TC.TC_ADDR , IF(TC.TC_POST IS NOT NULL, CONCAT(TC.TC_ADDR,' ',TC.TC_ADDR2),'') ADDR , TC.TC_SUMMARY , TC.TC_REGDT , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_CHANGENM,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') FS_PATH , IF(FS.FS_KEYNO IS NOT NULL, CONCAT('/resources/img/upload/',FS.FS_FOLDER,FS.FS_THUMBNAIL,'.',FS.FS_EXT),'/resources/tour/img/default/img_default_01.jpg') FS_THUMBNAIL_PATH FROM T_TOURISM_RESTAURANT TR LEFT JOIN T_TOURISM_COMMON TC ON TR.TR_TC_KEYNO = TC.TC_KEYNO LEFT JOIN T_COMMON_FILE_SUB FS ON TC.TC_THUMBNAIL = FS.FS_KEYNO WHERE TC.TC_USE = 'Y'
AND TR_DIVISION LIKE '%'||#{TR_DIVISION}||'%'
AND ( TC_TITLE LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_TITLE,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR TC_SUMMARY LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_SUMMARY,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
AND ( TC_TITLE LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_TITLE,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
AND ( TC_SUMMARY LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') OR REPLACE(TC_SUMMARY,' ','') LIKE CONCAT('%',REPLACE(#{searchKeyword},' ',''),'%') )
) A
ORDER BY A.TC_ADDR
) B ,(SELECT @rownum:=0)TMP
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
