mapper.sql:
FROM( SELECT A.AH_KEYNO NUM , A.AH_IP , A.AH_DESC , LEFT(AH_DATE,19) DATETIME , IFNULL(B.UI_ID,'비회원') UI_ID , C.MN_NAME HOMEDIV FROM T_ACTIVITY_HISTORY A LEFT JOIN T_USERINFO B ON A.AH_UI_KEYNO = B.UI_KEYNO LEFT JOIN T_MENU_MANAGER C ON A.AH_HOMEDIV_C = C.MN_KEYNO WHERE 1=1
AND AH_HOMEDIV_C = #{AH_HOMEDIV_C}
AND UI_ID = #{UI_ID}
AND AH_DATE >= #{searchBeginDate}
AND AH_DATE <= #{searchEndDate}
)AA WHERE 1=1
AND NUM LIKE CONCAT('%',#{item.searchKeyword},'%')
AND UI_ID LIKE CONCAT('%',#{item.searchKeyword},'%')
AND HOMEDIV = #{item.searchKeyword}
AND AH_DESC LIKE CONCAT( '%',#{item.searchKeyword},'%')
AND DATETIME LIKE CONCAT( '%',#{item.searchKeyword},'%')
AND AH_IP LIKE CONCAT( '%',#{item.searchKeyword},'%')
AND ( NUM LIKE CONCAT( '%',#{item.searchKeyword},'%') OR UI_ID LIKE CONCAT( '%',#{item.searchKeyword},'%') OR HOMEDIV = #{item.searchKeyword} OR AH_DESC LIKE CONCAT( '%',#{item.searchKeyword},'%') OR DATETIME LIKE CONCAT( '%',#{item.searchKeyword},'%') OR AH_IP LIKE CONCAT( '%',#{item.searchKeyword},'%') )

mapper.select:
	SELECT	*




 		ORDER BY	NUM ${sortDirect}


 		ORDER BY	UI_ID ${sortDirect}


 		ORDER BY	HOMEDIV ${sortDirect}


 		ORDER BY	AH_DESC ${sortDirect}


 		ORDER BY	DATETIME ${sortDirect}


 		ORDER BY	AH_IP ${sortDirect}


 		ORDER BY	NUM DESC



	 	LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
	-- LOG OFF
       	INSERT INTO T_ACTIVITY_HISTORY
