			 , AH_OS
		)
		VALUES
		(
	   		   #{AH_UI_KEYNO}
			 , #{AH_IP}
			 , NOW()
			 , #{AH_URL}
			 , #{AH_DESC}
			 , #{AH_MENU}
			 , #{AH_REFERER}
			 , #{AH_DEVICE}
			 , #{AH_AGENT}
			 , #{AH_SESSION}
			 , #{AH_HOMEDIV_C}
			 , #{AH_BROWSER}
			 , #{AH_OS}
		)

mapper.select:
	SELECT	*




 		ORDER BY	IP ${sortDirect}


 		ORDER BY	REFERER ${sortDirect}


 		ORDER BY	BROWSER ${sortDirect}


 		ORDER BY	OS ${sortDirect}


 		ORDER BY	DAYTIME ${sortDirect}


 		ORDER BY 	DAYTIME DESC



	 	LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

mapper.sql:
SELECT * FROM ( SELECT * FROM T_ACTIVITY_HISTORY WHERE 1=1
AND AH_HOMEDIV_C >= #{AH_HOMEDIV_C}
AND AH_DEVICE = 'P'
AND AH_DEVICE != 'P'
AND AH_OS != 'searchbot'
AND AH_DATE >= #{searchBeginDate}
AND AH_DATE <= #{searchEndDate}
GROUP BY AH_SESSION ORDER BY AH_KEYNO ) BODY ORDER BY AH_KEYNO DESC
