mapper.sql:
FROM( SELECT BT_KEYNO , BT_TYPE_NAME , CASE WHEN BT_HTML_YN = 'Y' THEN 'O' ELSE 'X' END BT_HTML_YN , CASE WHEN BT_THUMBNAIL_YN = 'Y' THEN 'O' ELSE 'X' END BT_THUMBNAIL_YN , CASE WHEN BT_REPLY_YN = 'Y' THEN 'O' ELSE 'X' END BT_REPLY_YN , CASE WHEN BT_COMMENT_YN = 'Y' THEN 'O' ELSE 'X' END BT_COMMENT_YN , CASE WHEN BT_SECRET_YN = 'Y' THEN 'O' ELSE 'X' END BT_SECRET_YN , CASE WHEN BT_UPLOAD_YN = 'Y' THEN 'O' ELSE 'X' END BT_UPLOAD_YN , CASE WHEN BT_EMAIL_YN = 'Y' THEN BT_EMAIL_ADDRESS ELSE 'X' END BT_EMAIL , CASE WHEN BT_HTMLMAKER_PLUS_YN = 'Y' THEN 'O' ELSE 'X' END BT_HTMLMAKER_PLUS_YN , BT_TICKET_PR_YN , BT_SNS_YN , BT_RSS_YN , SC_CODENM , (SELECT GROUP_CONCAT(MN_NAME) FROM T_MENU_MANAGER WHERE MN_BT_KEYNO = B.BT_KEYNO AND MN_DEL_YN = 'N') MN_NAME FROM T_BOARD_TYPE B LEFT JOIN ( SELECT * FROM T_COMMON_CODE_SUB WHERE SC_MC_IN_C = 'AC' ORDER BY SC_CODELV ) C ON B.BT_CODEKEY = C.SC_KEYNO WHERE BT_USE_YN = 'Y' )AA WHERE 1=1
AND BT_TYPE_NAME LIKE CONCAT('%',#{item.searchKeyword},'%')
AND SC_CODENM LIKE CONCAT('%',#{item.searchKeyword},'%')
AND MN_NAME LIKE CONCAT('%',#{item.searchKeyword},'%')
AND BT_HTML_YN = #{item.searchKeyword}
AND BT_THUMBNAIL_YN = #{item.searchKeyword}
AND BT_REPLY_YN = #{item.searchKeyword}
AND BT_COMMENT_YN = #{item.searchKeyword}
AND BT_SECRET_YN = #{item.searchKeyword}
AND BT_UPLOAD_YN = #{item.searchKeyword}
AND BT_HTMLMAKER_PLUS_YN = #{item.searchKeyword}
AND BT_EMAIL LIKE CONCAT('%',#{item.searchKeyword},'%')
AND ( BT_TYPE_NAME LIKE CONCAT('%',#{item.searchKeyword},'%') OR SC_CODENM LIKE CONCAT('%',#{item.searchKeyword},'%') OR MN_NAME LIKE CONCAT('%',#{item.searchKeyword},'%') OR BT_HTML_YN = #{item.searchKeyword} OR BT_THUMBNAIL_YN = #{item.searchKeyword} OR BT_REPLY_YN = #{item.searchKeyword} OR BT_COMMENT_YN = #{item.searchKeyword} OR BT_SECRET_YN = #{item.searchKeyword} OR BT_UPLOAD_YN = #{item.searchKeyword} OR BT_HTMLMAKER_PLUS_YN = #{item.searchKeyword} OR BT_EMAIL LIKE CONCAT('%',#{item.searchKeyword},'%') )

mapper.select:
	SELECT	*




 		ORDER BY	BT_TYPE_NAME ${sortDirect}


 		ORDER BY	SC_CODENM ${sortDirect}


 		ORDER BY	MN_NAME ${sortDirect}


 		ORDER BY	BT_HTML_YN ${sortDirect}


 		ORDER BY	BT_THUMBNAIL_YN ${sortDirect}


 		ORDER BY	BT_REPLY_YN ${sortDirect}


 		ORDER BY	BT_COMMENT_YN ${sortDirect}


 		ORDER BY	BT_SECRET_YN ${sortDirect}


 		ORDER BY	BT_UPLOAD_YN ${sortDirect}


 		ORDER BY	BT_HTMLMAKER_PLUS_YN ${sortDirect}


 		ORDER BY	BT_EMAIL ${sortDirect}



 		ORDER BY BT_KEYNO DESC



	 	LIMIT  	#{recordCountPerPage} OFFSET #{firstIndex}

SELECT * FROM	T_BOARD_TYPE
WHERE BT_USE_YN	=	'Y'
AND   BT_KEYNO  =	#{BT_KEYNO}

mapper.update:

UPDATE  T_BOARD_TYPE 
SET		BT_USE_YN			= 'N'
WHERE   BT_KEYNO			= #{BT_KEYNO}
