mapper.sql:
FROM ( SELECT AB.* , ROW_NUMBER() OVER (
ORDER BY COUNT ${sortDirect}
ORDER BY TA_DIVISION_NAME ${sortDirect}
ORDER BY TC_TITLE ${sortDirect}
ORDER BY TC_UP_CNT ${sortDirect}
ORDER BY TC_USE ${sortDirect}
ORDER BY UI_ID ${sortDirect}
ORDER BY TC_REGDT ${sortDirect}
ORDER BY COUNT DESC
) RNUM FROM( SELECT ROWNUM "COUNT", B.* FROM ( SELECT T.TC_KEYNO , D2.SC_CODENM TA_DIVISION_NAME , T.TC_TITLE , T.TC_UP_CNT , DECODE(TC_USE, 'Y','O','X') TC_USE , UI_ID , TO_CHAR(TC_REGDT,'yyyy-MM-dd HH24:mi:ss') TC_REGDT FROM T_TOURISM_COMMON T LEFT JOIN T_TOURISM_DESTINATION D ON T.TC_KEYNO = D.TA_TC_KEYNO LEFT JOIN T_COMMON_CODE_SUB D2 ON D.TA_DIVISION = D2.SC_KEYNO LEFT JOIN T_USERINFO U ON T.TC_REGNM = U.UI_KEYNO WHERE T.TC_TYPE = #{TC_TYPE} AND T.TC_USE != 'D' ORDER BY T.TC_REGDT ) B ) AB WHERE 1=1
AND COUNT LIKE '%' || #{item.searchKeyword} || '%'
AND TA_DIVISION_NAME = #{item.searchKeyword}
AND TC_TITLE LIKE '%' || #{item.searchKeyword} || '%'
AND TC_UP_CNT LIKE '%' || #{item.searchKeyword} || '%'
AND TC_USE LIKE '%' || #{item.searchKeyword} || '%'
AND UI_ID LIKE '%' || #{item.searchKeyword} || '%'
AND TC_REGDT LIKE '%' || #{item.searchKeyword} || '%'
AND ( COUNT LIKE '%' || #{item.searchKeyword} || '%' OR TA_DIVISION_NAME = #{item.searchKeyword} OR TC_TITLE LIKE '%' || #{item.searchKeyword} || '%' OR TC_UP_CNT LIKE '%' || #{item.searchKeyword} || '%' OR TC_USE LIKE '%' || #{item.searchKeyword} || '%' OR UI_ID LIKE '%' || #{item.searchKeyword} || '%' OR TC_REGDT LIKE '%' || #{item.searchKeyword} || '%' )
)AA

